css:
@import url('https://fonts.googleapis.com/css2?family=Oswald&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300&display=swap');
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body{
    font-family: 'Poppins', sans-serif;
}

body{
    overflow-x: hidden;
}
.custNav {
    display: block;
    background-color: #ffc809;
    height: 25px;
}

#Title1 {
    text-transform: uppercase;
    font-size: 34px;
    margin-top: -5px;
    background-color: #fff;
    text-align: center;
    width: 90%;
    font-weight: bold;
    font-family: 'Oswald', sans-serif;
}
p{
    font-weight: bold;
}
#invoiceTo {
    font-size: 20px;
    margin-bottom: 0;
}

#customerName{
    font-size: 15px;
}

#address{
    font-size: 10px;
    text-align: justify;
    color: #7d8c95;
}
.fllx{
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.flx{
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
}

#grey{
    color: #7d8c95;
}
.box{
    height: 140px;
    margin-bottom: 30px;
}

.box img {
    height: 100%;
    width: 140px;
}
.table{
    width: 90%;
    height: 300px;
    border: 1px solid #373b46;
}
table {
    font-family: arial, sans-serif;
    border-collapse: collapse;
    width: 100%;
    table-layout: fixed;
    overflow: hidden;
}

td, th {
    text-align: left;
    padding: 8px;
}

tr:nth-child(even) {
    background-color: #f2f2f2;
}

thead{
    background-color: #373b46;
    color: #fff;
}

tr {
    line-height: 25px;
}

.flx>div{
    width: 50%;
}

.fnsh{
    margin-top: 10px;
}

.flx.clr{
    position: relative;
    background-color: #ffc809;
    margin: auto;
}

.flx.clr::before{
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    left: -20px;
    width: 20px;
    background-color: #ffc809;
}

.flx.clr::after{
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    right: -300px;
    width: 300px;
    background-color: #ffc809;
}

.cc .custNav{
    height: 5px;
}

.sign {
    height: 1px;
    background-color: #373b46;
    position: relative;
}

.sign::after,
.sign::before {
    content: "";
    width: 20px;
    position: absolute;
    top: 0;
    bottom: 0;
    background-color: #fff;
}

.sign::before{
    left: 0;
}

.sign::after{
    right: 0;
}

#sign {
    display: block;
    margin-top: 10px;
    font-size: 13px;
    text-align: center;
    text-transform: capitalize;
    font-weight: bold;
}

.spans {
    display: flex;
    gap: 30px;
    font-size: 13px;
    text-align: center;
    text-transform: capitalize;
    font-weight: bold;
    margin-top: 20px;
    justify-content: center;

}
